mapper.select:
select user_name, gender, type,phone_number, is_linkman,dealer_name, identity, remark, area, city, bazaar, shop_type, business_type, stall, business_nature, passenger_car,  month_deal, month_installment, match_amount, detail_address,  create_by, create_time, update_by, update_time, id, state,three_car_state,two_car_state,one_car_state
from zyjr_car

     and user_name like concat('%', #{userName}, '%')
     and gender = #{gender}
     and phone_number = #{phoneNumber}
     and is_linkman = #{isLinkman}
     and identity = #{identity}
     and area like concat('%', #{area}, '%')
     and city like concat('%', #{city}, '%')
     and bazaar like concat('%', #{bazaar}, '%')
     and shop_type = #{shopType}
     and business_type = #{businessType}
     and stall = #{stall}
     and business_nature = #{businessNature}
     and passenger_car = #{passengerCar}
     and month_deal = #{monthDeal}
     and month_installment = #{monthInstallment}
     and match_amount = #{matchAmount}
     and detail_address = #{detailAddress}

     and dealer_name  like concat('%', #{dealerName}, '%')
     and state = #{state}
     and three_car_state = #{threeCarState}
     and two_car_state = #{twoCarState}
     and one_car_state = #{oneCarState}
     and type = #{type}

 select dealer_name, area,type, city, bazaar,  id, state,three_car_state,two_car_state,one_car_state,create_by, create_time
 from zyjr_car
where two_car_state="1" and create_by=#{userId}
